(2, 4, '_form', '<div class="row">\n<div class="col-xs-12 col-sm-12 col-md-12">[text* name class:form-control placeholder "Họ và Tên"]</div>\n<div class="col-xs-12 col-sm-12 col-md-6">[tel* phone class:form-control placeholder "Số điện thoại"]</div>\n<div class="col-xs-12 col-sm-12 col-md-6">[email* email class:form-control placeholder "Địa chỉ email"]</div>\n<div class="col-xs-12 col-sm-12 col-md-12">[text* nametour class:namet class:form-control placeholder "Tiêu đề"]</div>\n<div class="col-xs-12 col-sm-12 col-md-12">[textarea content class:form-control placeholder "Nội dung"]</div>\n<div class="col-xs-12 col-sm-12 col-md-12">[submit class:dattour "Gởi ngay"]</div>\n</div>'),
